      'ф'=>'f','х'=>'h','ц'=>'c','ч'=>'ch','ш'=>'sh',
      'щ'=>'shch','ю'=>'yu','я'=>'ya','ь'=>'', '`'=>'', '’'=>'',
      ' '=>'_', '-'=>'_', '\''=>'', ',' => '', '.' => '',
  ]);
  return $s;
}

